            french_days = {1 : 'Lun', 2 : 'Mar', 3 : 'Mer', 4 : 'Jeu', 5 : 'Ven', 6 : 'Sam', 7 : 'Dim'}
            english_days = { 1 : 'Mon', 2 : 'Tue', 3 : 'Wen', 4 : 'Thu', 5 : 'Fri', 6 : 'Sat', 7 : 'Sun'}

